def get_label(file_path):
    # Convert the path to a list of path components
    parts = tf.strings.split(file_path, os.path.sep)
    #parts - > tf.Tensor([b'E:' b'flower_photos' b'daisy' b'21652746_cc379e0eea_m.jpg'], shape=(4,), dtype=string)
    # The second to last is the class-directory
    one_hot = parts[-2] == class_names
    # Integer encode the label
    #one_hot -> tf.Tensor([ True False False False False], shape=(5,), dtype=bool)
    #tf.argmax(one_hot) -> tf.Tensor(0, shape=(), dtype=int64)
    return tf.argmax(one_hot)


